import 'controller/home_container_controller.dart';import 'package:flutter/material.dart';import 'package:unformatted_code_test/core/app_export.dart';import 'package:unformatted_code_test/presentation/chat_details_two_page/chat_details_two_page.dart';import 'package:unformatted_code_test/presentation/chats_one_page/chats_one_page.dart';import 'package:unformatted_code_test/presentation/home_page/home_page.dart';import 'package:unformatted_code_test/presentation/likes_page/likes_page.dart';import 'package:unformatted_code_test/presentation/search_page/search_page.dart';import 'package:unformatted_code_test/widgets/custom_bottom_bar.dart';class HomeContainerScreen extends GetWidget<HomeContainerController> {@override Widget build(BuildContext context) { return SafeArea(child: Scaffold(backgroundColor: ColorConstant.whiteA700, body: Navigator(key: Get.nestedKey(1), initialRoute: AppRoutes.homePage, onGenerateRoute: (routeSetting) => GetPageRoute(page: () => getCurrentPage(routeSetting.name!), transition: Transition.noTransition)), bottomNavigationBar: CustomBottomBar(onChanged: (BottomBarEnum type) {Get.toNamed(getCurrentRoute(type), id: 1);}))); } 
